const COMMANDS = {
	off:     '10100',
	low:     '00100',
	medium:  '01000',
	high:    '10000',
	light_1: '01010',
	light_2: '10010',
};
COMMANDS.light = COMMANDS.light_1;

const dip = msg.payload.id;
const command = COMMANDS[msg.payload.command];

const data = `${dip}${command}`;

let rawData = '';
for (const c of data) {
    rawData += `10${c}`;
}

return {
    symbol_time: 417,
    frequency: 304.2,
    modulation: 'OOK',
    repetitions: 10,
    repetition_delay: 10000,
    payload: rawData,
};

const MODULATIONS = {
    '2FSK': 0,
    'GFSK': 1,
    'ASK':  2,
    'OOK':  2,
    '4FSK': 3,
    'MSK':  4,
};

msg.payload = `
^F${ msg.frequency }
^M${ MODULATIONS[msg.modulation] }
^R${ msg.repetitions || 20 }
^T${ msg.reptition_delay || 10000 }
^E${ msg.symbol_time.toString(16).padStart(4, '0') }${ msg.payload }
`;

return msg;
