const re = /^sensor\.(.+)_(particulate_matter_.*_concentration|co2)$/;

const breakpoints = {};
breakpoints.aqi = [
    {
        lo: 0,
        hi: 50,
    },
    {
        lo: 51,
        hi: 100,
    },
    {
        lo: 101,
        hi: 150,
    },
    {
        lo: 151,
        hi: 200,
    },
    {
        lo: 201,
        hi: 300,
    },
    {
        lo: 301,
        hi: 400,
    },
    {
        lo: 401,
        hi: 500,
    },
]
breakpoints.particulate_matter_2_5um_concentration = [
    {
        lo: 0.0,
        hi: 12.0,
    },
    {
        lo: 12.1,
        hi: 35.4,
    },
    {
        lo: 35.5,
        hi: 55.4,
    },
    {
        lo: 55.5,
        hi: 150.4,
    },
    {
        lo: 150.5,
        hi: 250.4,
    },
    {
        lo: 250.5,
        hi: 350.4,
    },
    {
        lo: 350.5,
        hi: 500.4,
    },
];
breakpoints.particulate_matter_10_0um_concentration = [
    {
        lo: 0.0,
        hi: 54.0,
    },
    {
        lo: 55.0,
        hi: 154.0,
    },
    {
        lo: 155.0,
        hi: 254.0,
    },
    {
        lo: 255.0,
        hi: 354.0,
    },
    {
        lo: 355.0,
        hi: 424.0,
    },
    {
        lo: 425.0,
        hi: 504.0,
    },
    {
        lo: 505.0,
        hi: 604.0,
    },
];

const aqiValues = {};

for (const ent of msg.payload) {
    const m = ent.entity_id.match(re);
    if (!m) {
        continue;
    }
    const sensorName = m[1];
    const sensorType = m[2];

    const val = parseFloat(ent.state) - 1.0;

    if (isNaN(val)) {
        continue;
    }

    const valBreakpointsAll = breakpoints[sensorType];
    if (!valBreakpointsAll) {
        continue;
    }

    let i = valBreakpointsAll.length - 1;
    for (; i >= 0; i--) {
        if (val >= valBreakpointsAll[i].lo) {
            break;
        }
    }

    const valBreakpoints = valBreakpointsAll[i];
    const aqiBreakpoints = breakpoints.aqi[i];

    const bpRatio = (aqiBreakpoints.hi - aqiBreakpoints.lo) / (valBreakpoints.hi - valBreakpoints.lo);
    const aqi = Math.round((bpRatio * (val - valBreakpoints.lo)) + aqiBreakpoints.lo);

    const cur = aqiValues[sensorName];
    if (cur && cur.aqi > aqi) {
        continue;
    }

    aqiValues[sensorName] = {
        aqi,
        sensorName,
        sensorType,
    };
}
msg.payload = aqiValues;
return msg;

if (!msg.payload.print) {
    return;
}

const stage_names = [
    "Idle", // -1
    "Printing", // 0
    "Auto bed leveling",
    "Heatbed preheating",
    "Sweeping XY mech mode",
    "Changing filament",
    "M400 pause",
    "Paused due to filament runout",
    "Heating hotend",
    "Calibrating extrusion",
    "Scanning bed surface",
    "Inspecting first layer",
    "Identifying build plate type",
    "Calibrating Micro Lidar",
    "Homing toolhead",
    "Cleaning nozzle tip",
    "Checking extruder temperature",
    "Printing was paused by the user",
    "Pause of front cover falling",
    "Calibrating the micro lida",
    "Calibrating extrusion flow",
    "Paused due to nozzle temperature malfunction",
    "Paused due to heat bed temperature malfunction",
];

const stage_cur = msg.payload.print.stg_cur;
if (stage_cur === undefined) {
    return;
}

if (!isFinite(stage_cur)) {
    msg.reset = true;
    return msg;
}

msg.payload.stage_name = stage_names[stage_cur + 1] || "unknown";

if (stage_cur >= 0 ||
    msg.payload.print.nozzle_temper >= 80 ||
    msg.payload.print.bed_temper >= 40) {
    msg.reset = true;
} else {
    delete msg.reset;
}

return msg;

return {
    payload: {
        content: "Garage Door open > 15 minutes",
        options: {
            reply_markup: JSON.stringify({
                inline_keyboard: [[
                    {
                        text: "Close",
                        callback_data: "service|cover|close_cover|cover.garage_door",
                    },
                    {
                        text: "No action",
                        callback_data: "noop"
                    }]
                ]
            })
        },
    },
};

let service_call = undefined;

if (msg.payload.chatId !== -1001061749898) {
    return;
}

const content = msg.payload.content.split('|');

switch (content[0]) {
    case 'service':
        service_call = {
            payload: {
                domain: content[1],
                service: content[2],
                target: {
                    entity_id: [content[3]],
                },
            },
        };
        break;
    case 'noop':
        return;
}

return [service_call];

const out_msg = {
    payload: {
        type: 'editMessageReplyMarkup',
        content: '{}',
        options: {
            chat_id: msg.payload.chatId,
            message_id: msg.payload.messageId
        },
    }
};

return [out_msg];
